    #lastUpdateElement {
      color: @text;
    }

    a,
    a:visited {
      color: @accent;
    }

    input,
    textarea,
    select {
      background-color: @mantle;
      color: @text;
      caret-color: @text;
      border-color: @surface0;
    }

    .progress-bar-striped {
      background-image: linear-gradient(
        45deg,
        fade(@text, 40%) 25%,
        transparent 25%,
        transparent 50%,
        fade(@text, 40%) 50%,
        fade(@text, 40%) 75%,
        transparent 75%,
        transparent
      );
      background-color: @accent;
      color: @crust;
    }

    .form-control:focus {
      border-color: @accent;
      box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px @accent;
    }

    .table-striped > tbody > tr:nth-of-type(2n+1) {
      background-color: @surface0;
    }

    table#indexedOptionsTable tr td,
    .table > thead > tr > th {
      border-color: @surface2 !important;
    }

    table tr.optrow:focus-visible,
    table tr.optrow:hover {
      background-color: @surface1 !important;
    }

    .modal-content {
      background-color: @base;

      .modal-header,
      .modal-footer {
        border-color: @surface0 !important;
      }

      .close {
        color: @text;
        text-shadow: 0 1px 0 @text;
      }

      pre {
        color: @text;
        background-color: @mantle;
        border-color: @surface0;
      }
    }

    .btn-default {
      background: @surface0;
      color: @text;
      text-shadow: none;
      box-shadow: none;
      border-color: @surface0;

      &:hover,
      &:focus,
      &:active,
      .focus,
      .active {
        background-color: @surface1;
        color: @text;
        border-color: @surface0;
      }
    }

    footer {
      background-color: @mantle;
      color: @text;
      border-color: @surface2;
    }
  }
}

