      &:hover {
        color: @text;
        border-color: @surface1;
        background: darken(@surface2, 10%);
      }
    }

    .button.selected,
    .follow-button .unfollow .button {
      background-color: @accent;
      color: if(@flavor = latte, #fff, @crust);
      border-color: darken(@accent, 10%);

      &:hover {
        background-color: darken(@accent, 10%);
      }

      .app-info,
      #bd h3 {
        color: @subtext0;
      }

      #ft {
        color: @overlay0;
      }
    }
  }
