    /* Predictions */
    .fixed-prediction-button {
      color: @mantle !important;

      .channel-points-icon svg {
        color: @mantle !important;
      }

      &.fixed-prediction-button--disabled {
        color: @text !important;

        .channel-points-icon svg {
          color: @text !important;
        }
      }
