    [style*="rgb(255, 255, 255)"] {
      color: @base !important;
      .channel-root,
      .channel-info-content,
      .chat-author__display-name,
      &[data-a-target="chat-message-username"],
      &.message-author__display-name {
        color: @text !important;
      }
