    .shield-mode-icon svg path {
      fill: @peach;
    }

    .tray-highlight,
    .chat-input-highlight,
    .shield-mode-shortcut__btn {
      border-color: @peach !important;
    }

    /* Shield mode mod view button */

    .shield-mode-shortcut__inner,
    .shield-mode-view-toggle--active {
      color: @base;
      background: @peach;
    }

