      }
    }

    /* SUBtember subscribe button */

    .gift-subtember-button {
      background: @teal;
    }

    .player-controls when (@flavor = latte) {
      --color-text-button-overlay: @base !important;
      --color-background-range-overlay-fill: @base !important;
      --color-text-button-overlay-hover: @mantle !important;
    }

    /* Clips watch live prompt */

    .watch-live-prompt {
      h5 {
        color: @text !important;
      }
      h6 {
        color: @subtext0 !important;
      }
    }

    .clips-top-nav-user {
      .clips-top-nav-user__username,
      .tw-svg {
        color: @text !important;
      }
    }

    .chat-line__message--alert {
      border-color: @red !important;
      background-color: @base !important;

      .text-fragment--moderated-highlight {
        color: @text !important;
        background-color: fade(@red, 30%) !important;
      }
    }

    /* Activity feed icons */

    .activity-feed-v2-event--follow {
      color: @pink;
    }

    .activity-feed-v2-event--subscription {
      color: @blue;
    }

    .activity-feed-v2-event--reward-request, .activity-feed-v2-event--raiding {
      color: @yellow;
    }

    .activity-feed-v2-event--multi-stage {
      color: @teal;
    }

    .activity-feed-v2-event--creator-goal {
      color: @green;
    }

    /* Chat message highlights */

    div[style*="border"] {
      /* Suspicious user */
      &[style*="rgb(255, 130, 128)"] {
        border-color: @red !important;
      }

      /* First message */
      &[style*="rgb(255, 117, 230)"] {
        border-color: @pink !important;
      }

      /* Returning chatter */
      &[style*="rgb(0, 163, 163)"] {
        border-color: @teal !important;
      }

      /* Raider */
      &[style*="rgb(245, 245, 0)"] {
        border-color: @yellow !important;
      }

      /* Mention */
      &[style*="rgb(173, 173, 184)"] {
        border-color: @text !important;
      }

      /* Moderator */
      &[style*="rgb(0, 168, 101)"] {
        border-color: @green !important;
      }

      /* VIP */
      &[style*="rgb(219, 0, 179)"] {
        border-color: @mauve !important;
      }

      /* Subscriber */
      &[style*="rgb(82, 139, 255)"] {
        border-color: @blue !important;
      }
    }

    /* "Account suspended or deactivated" status banner */

    .viewer-card-header__account-status-banner {
      color: @text;
      background-color: @mantle !important;
    }

    /* Chat mentions */

    .mention-fragment {
      color: @text;
      background-color: @surface0;

      &.mention-fragment--recipient {
        color: @base;
        background-color: @text;
      }
    }
  }
}

@-moz-document domain("dashboard.twitch.tv") {
