      --color-background-button-overlay-hover: @base !important;
      --color-background-button-icon-overlay-hover: fade(@base, 13%);
      --color-background-button-overlay-primary-hover: fade(
        @base,
        13%
      ) !important;
    }

    .simplebar-content {
