    @surface0: @catppuccin[@@flavor][@surface0];
    @base: @catppuccin[@@flavor][@base];
    @mantle: @catppuccin[@@flavor][@mantle];
    @crust: @catppuccin[@@flavor][@crust];
    @accent: @catppuccin[@@flavor][@@accentColor];

    color-scheme: if(@flavor = latte, light, dark);

    ::selection {
      background-color: fade(@accent, 30%);
    }

    input,
    textarea {
      &::placeholder {
        color: @subtext0 !important;
      }
    }

    [bgcolor="#fafaf0"],
    [bgcolor="#f6f6ef"] {
      background-color: @base;
    }
    body {
      background-color: @mantle;
      color: @text;
    }

    td {
      color: @text;
    }

    /* Header */
    td[bgcolor="#ff6600"] {
      background-color: @accent;

      .pagetop,
      .pagetop a {
        color: @crust !important;
      }

      img[src="y18.svg"] {
        @svg: escape(
          '<svg height="18" viewBox="4 4 188 188" width="18" xmlns="http://www.w3.org/2000/svg"><path d="M4 4h188v188H4z" fill="@{accent}"/><path d="M73.252 45.01 96 92.401l22.748-47.391h19.566l-34.324 64.487v41.493H88.01v-41.493L53.686 45.01z" fill="@{crust}"/></svg>'
        );
        content: url("data:image/svg+xml,@{svg}");
        border-color: @crust !important;
      }

      [color="#ffffff"] {
        color: @crust;
      }
    }

    td[bgcolor="#ffffaa"] {
      background-color: @yellow;

      td {
        color: @crust;
      }
    }

    .subtext,
    .comhead {
      &,
      a:link,
      a:visited,
      a:hover {
        color: @overlay2 !important;
      }
    }

    a:link {
      color: @blue;
      &:hover {
        color: @sky;
      }
    }
    .hnmore a:link,
    a:visited {
      color: @lavender;
    }

    [color="#3c963c"] {
      color: @green;
    }

    /* Story points */
    .score {
      color: @subtext0;
    }

    /* Story ranking number */
    .title {
      color: @lavender;
    }

    /* Comment text */
    .commtext {
      color: @text;
    }

    /* Comment box */
    input,
    textarea,
    select {
      background-color: @mantle;
      color: @text;
      border-width: 1px;
      border-style: solid;
      border-color: @surface0;
      border-radius: 2px;
    }

    input {
      padding: 2px;
    }

    /* Poll questions */
    td.comment > div > font {
      color: @text;
    }

    /* Text post content */
    .toptext {
      color: @text;
    }

    .votearrow {
      @svg: escape(
        '<svg xmlns="http://www.w3.org/2000/svg" height="32" viewBox="0 0 32 16" width="32"><path d="M2 27 16-2l14 29z" fill="@{subtext0}"/></svg>'
      );
      background-image: url("data:image/svg+xml,@{svg}");
    }

    .yclinks {
      color: @surface2;
    }

    b {
      color: @text;
    }

    table[bgcolor="#ff6600"] {
      background-color: @accent;
    }
  }
}

